void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 tex = texture(iChannel0,uv);
    vec4 tex2 = texture(iChannel1,uv);

	fragColor = vec4(tex);

    if(fragColor.y > .55 && fragColor.x<.4 && fragColor.z<.5)
        fragColor=vec4(tex2);
}
