#define MAX_ITER 100
#define EPS 0.00001
#define MAX_DIST 20.0
#define PI2 6.28318530718 

vec3 hsv2rgb(in vec3 c) {
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0);
	return c.z * mix(vec3(1.0), rgb, c.y);
}

float sphere(vec3 pos, float radius) {
    return length(pos) - radius;
}

float distfunc(vec3 pos, vec4 o1) {
	return sphere(pos - o1.xyz, o1.w);
}

vec3 surfnorm(vec3 pos, vec4 o1) {
    vec2 eps = vec2(0.0, EPS);
	return normalize(vec3(
    	distfunc(pos + eps.yxx, o1) - distfunc(pos - eps.yxx, o1),
	    distfunc(pos + eps.xyx, o1) - distfunc(pos - eps.xyx, o1),
    	distfunc(pos + eps.xxy, o1) - distfunc(pos - eps.xxy, o1)
    ));
}

vec3 calcray(vec3 campos, vec3 camtgt, vec3 camup, vec2 st) {
    vec3 camdir = normalize(camtgt - campos);
    vec3 camright = normalize(cross(camup, campos));
	camup = cross(camdir, camright);

    return normalize(camright * st.x + camup * st.y + camdir);
}

vec4 raymarch(vec3 campos, vec3 raydir, vec4 o1) {
	float totaldist = 0.0;
	vec3 pos = campos;
	float dist = EPS;

	for (int i = 0; i < MAX_ITER; i++) {
    	if (dist < EPS || totaldist > MAX_DIST)
        	break;

    	dist = distfunc(pos, o1);
	    totaldist += dist;
    	pos += dist * raydir;
    }

    return vec4(pos, dist);
}

vec2 normuv(in vec2 uv, in vec2 res) {
	uv = -1.0 + 2.0 * uv.xy / res.xy;
    uv.x *= res.x / res.y;

    return uv;
}

float normsin(float x) {
    return sin(x) * 0.5 + 0.5;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = -normuv(fragCoord.xy, iResolution.xy);
	vec2 mouse_uv = iMouse.xy / iResolution.xy;

    vec3 campos = vec3(0, EPS, 0);
    vec3 camtgt = vec3(0.0, 1.0, 0.0);
    vec3 camup = vec3(0.0, 0.0, 1.0);

    float angle = iTime * 0.5;
    mat2 rmat = mat2(
        cos(angle), -sin(angle),
        sin(angle), cos(angle)
    );

	camup.xz = rmat * camup.xz;

    vec4 sphere_obj = vec4(0.0, 0.0, 0.0, 1.0);

    vec3 st_raydir = calcray(campos, camtgt, camup, uv);
    vec3 mouse_raydir = calcray(campos, camtgt, camup, mouse_uv);

    vec4 stmarch = raymarch(campos, st_raydir, sphere_obj);
    stmarch.y *= exp(sin(iTime) * 10.0);
    vec4 msmarch = raymarch(campos, mouse_raydir, sphere_obj);

    vec3 color = vec3(0.0, 0.0, 0.0);

    if (stmarch.w < EPS) {
        vec3 norm = surfnorm(stmarch.xyz, sphere_obj);

        float az = atan(stmarch.z / stmarch.x);
        float inc = acos(stmarch.y / length(stmarch.xyz));
		float d = acos(cos(az) * cos(abs(inc)));
        float twist = pow(inc, 4.0);

        color = hsv2rgb(vec3(
            normsin((twist + iTime) * 2.0),
            1.0, 
            pow(normsin((az+twist+iTime)*2.0),0.25)
        ));
    }

    fragColor = vec4(color, 1.0);
}

#define PI2 6.28318530718 
#define NHARM 10
#define EPS 0.0001

float normsin(float x) {
    return sin(x) / 2.0 + 0.5;
}

vec2 mainSound(float time)
{
    float freqs[NHARM];
    float amps[NHARM];
    float phases[NHARM];
    float fundamental = 1000.0;
    float fnharm = float(NHARM);
    float gain = 0.1;

    float flatness = exp(normsin(time));

    for (int i = 0; i < NHARM; i++) {
        float fi = float(i) / fnharm;
        float fp = (fi + 1.0) / fnharm;
        freqs[i] = fundamental * fi;
       	amps[i] = (gain / fnharm) / (fp - normsin(flatness * fi + flatness));
        phases[i] = sin(pow(normsin(flatness+fi),cos(flatness+fi) * 10.0));
    }

    float amp = 0.0;

    for (int i = 0; i < NHARM; i++) {
        amp += sin(time * PI2 * freqs[i] + phases[i]) * amps[i];
    }

    return vec2(amp,amp);
}
