float time = iTime;
float margin = .03;
float mHalf = margin * .5;
float mTop = 1. - margin;
float mBot = margin;

float atRes(float val) {
	return val;//floor(val*res)/res;
}

vec3 getPixel(float x, float y){
    float ss = mHalf; // Sample size
	return (
        	// Center
        	texture(iChannel0, vec2(x,	y)).xyz +

        	// Sides
        	texture(iChannel0, vec2(x,	y-ss)).xyz +
        	texture(iChannel0, vec2(x+ss,	y)	).xyz +
        	texture(iChannel0, vec2(x,	y+ss)).xyz +
        	texture(iChannel0, vec2(x-ss,	y)	).xyz

        	// Diagonals
        	//texture(iChannel0, vec2(x+ss,y-ss)).xyz +
            //texture(iChannel0, vec2(x+ss,y+ss)).xyz +
        	//texture(iChannel0, vec2(x-ss,y+ss)).xyz +
        	//texture(iChannel0, vec2(x-ss,y-ss)).xyz
           )/5.;	// Since 3 of them will be black -- Outside bounds --.
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 cord = fragCoord.xy;
    cord -=iResolution.xy/2.;
	vec2 uv = cord / iResolution.xy * (iResolution.x/iResolution.y);
    vec3 rgb = vec3(0.,0.,0.);

    if(abs(uv.x) > .5 && abs(uv.y) > .5)
        rgb = getPixel(uv.x < 0. ? mBot : mTop,uv.y < 0. ? mBot : mTop);
    else if(uv.x < -.5)
        rgb = getPixel(mBot,atRes(uv.y+.5));
    else if(uv.x > .5)
    	rgb = getPixel(mTop,atRes(uv.y+.5)).xyz;
    else if (uv.y < -.5)
    	rgb = getPixel(atRes(uv.x+.5),mBot).xyz;
    else if (uv.y > .5)
        rgb = getPixel(atRes(uv.x+.5), mTop).xyz;
    else if( abs(uv.x) < .47 && abs(uv.y) < .46)
		rgb = texture(iChannel0, vec2(uv.x+.5, uv.y+.5) ).xyz;

    rgb -= abs(uv.x) > .5 || abs(uv.y) > .5 ? pow(pow(abs(uv.y),5.)+pow(abs(uv.x),5.),2.)*50.
        : 0.;

	fragColor = vec4(rgb,1.0);
}
