void mainImage( out vec4 fragColor, in vec2 p ){
    p =  (-iResolution.xy + 2.0 * p.xy )/iResolution.y;
    p.y = abs( p.y +sin(iTime) );
    vec3 col = vec3( 1.0 * 1.-p.y );
    p.x/=p.y - 2.;
    p.x+=iTime;
    if( mod( p.x, 2. ) < 1. ) col.yx=vec2(0.);
	fragColor = vec4( col, 1.0 );
}
