#define DEBUG 0
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float ratio = iResolution.x / iResolution.y;
    uv.x *= ratio;
    vec2 clickUV = iMouse.xy / iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(0.0);

    float k = 5.0;
    float b = 0.0;
    float b1 = b;
    float kSign = 1.0;
    float width = 0.02;
    if (k < 0.0) kSign = -1.0;
    float b2 = b1 - width * sqrt(k * k + 1.0) * kSign;

    float leftBound = abs((1.0 - b2) / k);
    float totalLength = 1.0 * ratio + leftBound;; // 这个域映射时，考虑 ratio.
    if (k < 0.0) totalLength = 1.0 * ratio + leftBound + width * 2.0;

    float speed = 1.0;
    float driver = fract(iTime / speed);
#if DEBUG == 1
    driver += clickUV.x; 
#endif
    float x = uv.x + leftBound - driver * totalLength;
    if (k < 0.0) x = uv.x - driver * totalLength + width;
    float y = uv.y;
    float y1 = k * x + b1;
    float y2 = k * x + b2;
    if (kSign > 0.0 && y < y1 && y > y2 || kSign < 0.0 && y < y2 && y > y1) col = vec3(1.0);
    else col = vec3(0.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}
