void mainImage(out vec4 O, vec2 I)
{
    vec3 a = iResolution.xxy,
    p = vec3(iTime/.1, cos(iTime+a)),
    i = a-a,
    d = a;
    d.yz -= round(I)*2.;

    for(d/=a.y; i.x++<9e2 && texture(iChannel0, a = ceil(p)/3e2).r > length(a.yz)/.1;
    p += d*min(a = fract(-abs(p))/abs(d)+3e-4, min(a.y,a.z)).xxx );
    O.rgb = fwidth(p);
}
