//returns 1 for inside circ, 0 for outside
float circle(in vec2 _st, in vec2 pos, in float _radius){   
    vec2 dist = _st - pos;    
	return 1. - smoothstep(_radius, _radius + _radius/100.0, dot(dist,dist)*4.0);
}

float random1d(float n){
	return fract(sin(n) * 43758.5453);
}

float random2d(vec2 n) { 
    return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

//1D NOISE 
//returns 0 - 1
float noise1d(float p){
	float fl = floor(p);
	float fc = fract(p);
	return mix(random1d(fl), random1d(fl + 1.0), fc);
}

//2D NOISE cheap value noise
//returns 0 - 1
float noise2d(vec2 p){
    vec2 ip = floor(p);
    vec2 u = fract(p);
    u = u*u*(3.0-2.0*u);

    float res = mix(
        mix(random2d(ip),random2d(ip+vec2(1.0,0.0)),u.x),
        mix(random2d(ip+vec2(0.0,1.0)),random2d(ip+vec2(1.0,1.0)),u.x),u.y);
    return res*res;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //define greyscale colors
    vec3 bgCol = vec3(0.3);
    vec3 circCol = vec3(0.8);

    //warp uv space to make the circle warp based on uv coord and time
    vec2 warpUv = uv + noise2d(uv * 3. + iTime) * 0.1;

    //draw circle in center with 0.4 radius using the warped uv space
    float circ = circle(warpUv,vec2(0.5),0.4);    
    vec3 col = mix(bgCol,circCol,circ);

    //add grain
    //modulate hi-res noise for grain with a low res noise for the gradients
    float grain = noise2d(uv * 1000. + iTime) * noise2d(uv * 6. + iTime);
    //only draw noise inside a 0.35 radius circle and turn down strength
    float circInner = circle(warpUv,vec2(0.5),0.35);
    grain *= circInner * 0.6;
    col -= grain;

    // Output to screen
    fragColor = vec4(col,1.0);
}
