    Shader:

    cubemap to fisheye 
	https://www.shadertoy.com/view/lssGD4

    Cubemap debug display
    https://www.shadertoy.com/view/tdjXDt

　　Reference：

	URL:
	MSDN 立方体環境マッピング（Direct3D 9）
	https://docs.microsoft.com/ja-jp/windows/win32/direct3d9/cubic-environment-mapping?redirectedfrom=MSDN

	Scali's OpenBlog™
	Cubemaps
	https://scalibq.wordpress.com/2013/06/23/cubemaps/

	OpenGLでequirectangular形式の画像を扱う場合の作法
	https://qiita.com/meronpan/items/d240c0f831cd8b6fe9c4
*/

/*
2019/11/15   
  1.0.0 → 1.0.1
	・keyboard input 対応

2019/11/11   
  0.0 → 1.0.0
	・最初のリリース
*/

bool keyPress(int ascii){
    return (texture(iChannel1,vec2((.5+float(ascii))/256.,0.25)).x > 0.);
}


const float PI = 3.1415926535897932384626433832795;

// from : cubemap to fisheye https://www.shadertoy.com/view/lssGD4
// Adapted from http://strlen.com/gfxengine/fisheyequake/
vec3 fisheye_lookup(float fov, vec2 position)
{
	vec2 d = position - 0.5;
    d.x /= iResolution.y / iResolution.x;

	float yaw = sqrt(d.x*d.x+d.y*d.y) * fov;

	float roll = -atan(d.y, d.x);
	float sx = sin(yaw) * cos(roll);
	float sy = sin(yaw) * sin(roll);
	float sz = cos(yaw);	

	return vec3(sx, sy, sz);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv  = vec2(2.) * fragCoord/iResolution.xy - vec2(1.);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv2 = fragCoord/iResolution.xy;

    vec3 col = vec3(0);
    int wait_sec=2;
    int mode = int(mod(iTime,3.*float(wait_sec)))/wait_sec; 

    // keyboard
    if(keyPress(48)) mode = 0;
    if(keyPress(49)) mode = 1;
    if(keyPress(50)) mode = 2;

    if(mode == 0)
    {
        // 正距円筒図法
        // equirectangular

        // 経度 -180 ～ 180 , 緯度-90 ～ 90
    	vec2 thetaphi  = uv * vec2(PI,PI/2.);

        col = texture(iChannel0, vec3( cos(thetaphi.y) * cos(thetaphi.x),
                                       sin(thetaphi.y),
                                       cos(thetaphi.y) * sin(thetaphi.x)) ).rgb;
    }
    else if(mode == 1){
        // スフィアマップ
        // sphere 
        col = texture(iChannel0, vec3( fisheye_lookup( radians(-360.), uv2) ) ).rgb;
        uv.x /= iResolution.y / iResolution.x;
        float d = distance(0.0, uv.x * uv.x + uv.y * uv.y);
        if(d > 1.0){
            col = vec3(0.);
        }
    }
    else if(mode == 2){
        // 展開図
        // cubemap Unfold
        int x = int(floor(uv2.x / (1.0/4.)));
        int y = int(floor(uv2.y / (1.0/3.)));
        float offsetX = 1./4. * float(x);
        float offsetY = 1./3. * float(y);
        //  0.0 ～ 1.0
        vec2 st = (uv2 - vec2(offsetX,offsetY)) * vec2(4.0, 3.0);
        // -1.0 ～ 1.0
        st = (st - 0.5) * 2.;

        if(y == 0 && x == 1){
            // Bottom
            col = texture(iChannel0,vec3(st.x, -1, st.y)).rgb;
        }
        else if(y == 1)
        {
            if(x == 0){
                // Left
                col = texture(iChannel0,vec3(-1.0, st.y, st.x)).rgb;
            }
            else if(x == 1) {
                // Front
               col = texture(iChannel0,vec3(st.x, st.y, 1)).rgb;
            }
            else if(x == 2){
                // Right
                col = texture(iChannel0,vec3(1.0, st.y, -st.x)).rgb;
            }
            else if(x == 3){
                // Back
                col = texture(iChannel0,vec3(-st.x, st.y, -1)).rgb;
            }
        }
        else if(y == 2 && x == 1){
            // Top
            col = texture(iChannel0,vec3(st.x,  1, -st.y)).rgb;
        }        
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
