// constrain viewport to this many pixels:
vec2 window = vec2(160,120.);

// scale in nearest-neighbour by this factor
float scale_factor = 4.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 zoom = window / iChannelResolution[0].xy;
    vec2 uv = fragCoord * zoom - vec2(0.5);
    vec2 f = fract(uv);
    uv -= f;
    f = (f - 0.5) * scale_factor;
#if 0
    f = smoothstep(0., 1., f);  // just for fun
#else
    f = clamp(f, 0.0, 1.0);
#endif
    uv += f;
    uv = (uv + vec2(0.5)) / iResolution.xy;
    fragColor = texture(iChannel0, uv);
}
