const float BLURDIST_PX = 64.0;
const int NUM_SAMPLES = 128;
const float NUM_SAMPLES_F = float(NUM_SAMPLES);

vec3 srgb2lin(vec3 c) { return c*c; }
vec3 lin2srgb(vec3 c) { return sqrt(c); }

float remap( float a, float b, float t )
{
    return clamp( (t-a)/(b-a), 0.0, 1.0);
}

//////////////////////////////////////////////////////
//note: from https://www.shadertoy.com/view/4d2Xzw
// The Golden Angle is (3.-sqrt(5.0))*PI radians
const float GOLDEN_ANGLE = 2.39996323;
mat2 rot = mat2(cos(GOLDEN_ANGLE), sin(GOLDEN_ANGLE), -sin(GOLDEN_ANGLE), cos(GOLDEN_ANGLE));
vec3 Bokeh(sampler2D tex, vec2 uv, float radius)
{
	vec3 sumcol = vec3(0.0);
    vec2 rcp_pixelsiz = 1.0 / iResolution.xy;
    float r = 1.0;
    vec2 vangle = vec2(0.0, radius / float(NUM_SAMPLES/2)); //TODO: NUM_SAMPLES/2 is a random scale factor

	for (int j = 0; j < NUM_SAMPLES; j++)
    {  
        r += 1.0 / r; //TODO: what does this recursive series converge to?
	    vangle = rot * vangle;
        vec2 ofs = (r-1.0) * rcp_pixelsiz * vangle;
		sumcol += srgb2lin( texture(tex, uv + ofs, -10.0).rgb );
	}
	return sumcol / float(NUM_SAMPLES);
}

//////////////////////////////////////////////////////
//note: from http://blog.tuxedolabs.com/2018/05/04/bokeh-depth-of-field-in-single-pass.html
const float RAD_SCALE = 0.5; // Smaller = nicer blur, larger = faster
vec3 Bokeh_tuxedo(sampler2D tex, vec2 uv, float blursiz)
{
    vec3 color = texture(tex, uv).rgb;
	float tot = 1.0;
	float radius = RAD_SCALE;
	for (float ang = 0.0; radius<blursiz; ang += GOLDEN_ANGLE)
	{
		vec2 tc = uv + vec2(cos(ang), sin(ang)) / iResolution.xy * radius;
		vec3 sampleColor = srgb2lin(texture(tex, tc, -10.0).rgb);
		//float m = smoothstep(radius-0.5, radius+0.5, blursiz); //note: from org
        float m = remap(radius-0.5, radius+0.5, blursiz);
		color += mix(color/tot, sampleColor, m);
		tot += 1.0;
        radius += RAD_SCALE/radius; //TODO: what does this recursive series converge to?
	}
	return color /= tot;
}

//////////////////////////////////////////////////////

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    float sinblur = ( 0.55 + 0.45 * sin( 5.0 * uv.x + iTime ) );
    float blurdist = (iMouse.z>0.5) ? BLURDIST_PX * iMouse.x/iResolution.x : BLURDIST_PX * sinblur;

    //vec3 sumcol = Bokeh(iChannel0, uv, blurdist);
    vec3 sumcol = Bokeh_tuxedo(iChannel0, uv, blurdist/2.0); //TODO: div2 is... uh, empirical.

    fragColor = vec4( lin2srgb(sumcol), 1.0 );
}

#define USE_RANDOM

const float blurdist_px = 64.0;
const int NUM_SAMPLES = 16;

const float THRESHOLD = 0.1;
const float MULT = 4.0;

vec3 srgb2lin(vec3 c) { return c*c; }
vec3 lin2srgb(vec3 c) { return sqrt(c); }

//note: uniform pdf rand [0;1[
float hash12n(vec2 p)
{
	p  = fract(p * vec2(5.3987, 5.4421));
    p += dot(p.yx, p.xy + vec2(21.5351, 14.3137));
	return fract(p.x * p.y * 95.4307);
}

vec4 pattern( vec2 p )
{
    float aspect = iResolution.x/iResolution.y;
    float p0 = step(abs(p.x-0.125), 0.01) * step(abs(p.y-0.27), 0.01);
    float p1 = step( length( p-vec2(0.125, 0.45) ), 0.025 );

    float p2_0 = step( length( p-vec2(0.08, 0.14) ), 0.0125 );
    float p2_1 = step( length( p-vec2(0.16, 0.125) ), 0.0125 );
    float p2_2 = step( length( p-vec2(0.1, 0.07) ), 0.0125 );
    float p2 = max(p2_0, max(p2_1,p2_2));

    return vec4( max( p0, max(p1,p2) ) );
}

vec3 sampletex( vec2 uv )
{
    float t = fract( 0.1*iTime );
    if ( t < 1.0/3.0)
    	return srgb2lin( texture( iChannel0, uv, -10.0 ).rgb );
    else if ( t < 2.0/3.0 )
        return srgb2lin( texture( iChannel1, uv, -10.0 ).rgb );
    else
	    return srgb2lin( texture( iChannel2, uv, -10.0 ).rgb );    
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 suv = fragCoord / iResolution.xy; 
    vec2 uv = fragCoord / iResolution.xx;

    vec3 sumcol = vec3(0);
    if ( suv.x < 0.25 )
        sumcol += pattern( uv ).rgb;
    else
    {
        vec3 smpl = (sampletex(uv) - THRESHOLD) / (1.0-THRESHOLD);
        sumcol += smpl*smpl; //wtf
    }
    //fragColor = vec4( lin2srgb(sumcol), 1.0 ); return;

    fragColor = vec4( lin2srgb( sumcol * MULT ), 1.0 );
}
