//This parameter changes the magnification.
const float mag = 9.0;
const float GREEN = 0.;
const float BLUE = 1.;
const float GRAY = 2.;
const float YELLOW = 3.;
const float WHITE = 4.;
const float RED = 5.;


//this function is from https://www.shadertoy.com/view/4djSRW
float hash12(vec2 p)
{
    float num_colors = 5.0;
    //set the number of colors to be randomly generated

    vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return floor(fract((p3.x + p3.y) * p3.z)*num_colors);
}

float magnify(vec2 fragCoord,float mag){
    float mag1 = (iTime)/(mag*mag);
    fragCoord = floor(fragCoord/mag);
    fragCoord += mag*mag*vec2(sin((fragCoord.y*mag1)),sin((fragCoord.x*mag1)));
	fragCoord *= mag;
    return hash12(floor(fragCoord/pow(3.0,mag)));
}

vec4 get_neighbors(vec2 fragCoord,float mag){
    return vec4(magnify(fragCoord+vec2(0,1),mag),
        magnify(fragCoord+vec2(1,0),mag),
        magnify(fragCoord-vec2(0,1),mag),
        magnify(fragCoord-vec2(1,0),mag));
}


bool is_next_to(float color,vec4 neighbors){
	return (neighbors.x == color)
        || (neighbors.y == color)
        || (neighbors.z == color)
        || (neighbors.w == color);
}



vec3 get_color(float color){
    return color == BLUE
    	? vec3(0.149,0.141,0.912)
    :color == GREEN
    	? vec3(0.000,0.833,0.224)
   	:color == RED
    	? vec3(1.0,0.0,0.0)
   	:color == WHITE
    	? vec3(1.0,1.0,1.0)
   	:color == GRAY
    	? vec3(192.0,192.0,192.0)/255.0
    :color == YELLOW
    	? vec3(1.0,1.0,0.0)
   	:vec3(0);
}

float iterations(float color1,in vec2 fragCoord,float mag){
    fragCoord /= 3.0;
    float colors[] = float[](GREEN,BLUE,GRAY,YELLOW,WHITE,RED);
    vec4 neighbors;
    while(mag > 1.0){
    neighbors = get_neighbors(fragCoord,mag);
    color1 = color1 == GREEN && is_next_to(BLUE,neighbors)
    	? YELLOW
    :color1 == BLUE && is_next_to(GREEN,neighbors)
        ? GREEN
    :color1 == YELLOW && is_next_to(GRAY,neighbors)
        ? BLUE
    :color1 == GRAY && is_next_to(YELLOW,neighbors)
        ? YELLOW
    :color1 == WHITE && is_next_to(YELLOW,neighbors)
        ? GRAY
    :color1 == GRAY && is_next_to(BLUE,neighbors)
        ? BLUE
    :color1;
    mag -= 1.0;
    }
    return colors[int(color1)];
}

void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    fragCoord *= mag*(((iMouse.x-iMouse.y)+iResolution.x)/iResolution.x);
    fragCoord = fragCoord + floor(iTime*100.0)*vec2(mag,0.0);
    float color1 = magnify(fragCoord,mag);
    //color1 = replace_pattern(mat2(1.0,1.0,1.0,1.0),mat2(0.0,2.0,2.0,0.0),fragCoord,mag);

    color1 = iterations(color1,fragCoord,mag-1.0);

    fragColor = vec4(get_color(color1),1.0);
}
