/**WORK IN PROGRESS*/

// common utils
#define PI             				3.141592
#define layout(vec) 				(2.* vec - iResolution.xy) / iResolution.y;
#define pixels_to_uv(pixelLength) 	pixelLength / iResolution.y;
#define degreeOf(uv)				atan(uv.y, uv.x) / PI * 180.0;

// shader uniforms
#define radius_in_pixels     		320.0
#define hole_radius_in_pixels  		160.0
#define minDegree         			-30.0           	// [-180..180]
#define degree1         			80.0            	// [-180..180]
#define degree2         			190.0           	// [0..360]
#define maxDegree         			220.0           	// [0..360]   
#define violet           			vec3(0.8, 0.8, 1.0)
#define yellow           			vec3(1.0, 0.8, 0.2)
#define red           				vec3(1.0, 0.0, 0.0)

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // layout
  	vec2 uv = layout(fragCoord);

    // 1. convert uniforms from pixels to uv 
    float radius      = pixels_to_uv(radius_in_pixels);
    float hole_radius = pixels_to_uv(hole_radius_in_pixels);

    // 2. draw anti-aliased circle
    float d = length(uv);
    float wd = fwidth(d);
    float circle = smoothstep(radius + wd, radius - wd, d);

    // 3. draw colored segments
    vec3 color;
    float degree = degreeOf(uv);
    if (-180.0 < degree && degree < (maxDegree - 360.0)) degree += 360.0;    
    if (minDegree < degree && degree < degree1) {
      color = violet;
    } else if (degree1 < degree && degree < degree2) {
      color = yellow;
    } else if (degree2 < degree && degree < maxDegree) { 
      color = red;
    }
    vec3 circleColor = circle * color;  

    // 4. draw hole inside circle
    circleColor -= vec3(smoothstep(hole_radius + wd, hole_radius - wd, d));    

    // result
    fragColor = vec4(circleColor, 1);    
}
