void mainImage (out vec4 fragColor, in vec2 fragCoord) {

    // Youi Labs GPU precision shader (slightly modified)
    // https://www.youi.tv/mobile-gpu-floating-point-accuracy-variances/

    vec2 positionZeroToOneNormalized = fragCoord / iResolution.xy;
    float horizontalPositionOneToZero = 1.0 - positionZeroToOneNormalized.x;


    float vertical0to26 = positionZeroToOneNormalized.y * 26.0;
    float verticalInteger0to26 = floor(vertical0to26);

    float verticalPowerOf2 = exp2(verticalInteger0to26); // 1, 2, 4, 8, 16, 32, 64...

    // 
    //              1.00         0.75 0.50 0.25 0.00 <- horizontalPositionOneToZero
    //
    // 2^26  67108865.00  67108864.75  67108864.50  67108864.25  67108864.00
    //
    // 2^25  33554433.00  33554432.75  33554432.50  33554432.25  33554432.00
    //
    //  ...          ...          ...          ...          ...          ...
    //
    //  2^2         5.00         4.75         4.50         4.25         4.00
    //
    //  2^1         3.00         2.75         2.50         2.25         2.00
    //
    //  2^0         2.00         1.75         1.50         1.25         1.00
    //
    float horzExpToExpPlusOneVertTo26 = verticalPowerOf2 + horizontalPositionOneToZero; // 1 + 1,

    float fractionOfExponent = fract(horzExpToExpPlusOneVertTo26);

    if (fract(vertical0to26) > 0.07) {
    	fragColor = vec4(fractionOfExponent, fractionOfExponent, fractionOfExponent, 1.0);
    } else {
        // Green separators to ease the counting
        fragColor = vec4(1.0, 1.0, 1.0, 1.0);

    }
}
