// Beauty of hamming dists...
// - alwas 20200115
int ham(int a, int b) {
    int h=0;    
    for (int c = 32; c >= 0; c--){
      int ak = a >> c;
      int bk = b >> c;
      if ((ak & 1) != (bk & 1))
          h++;
    }    
    return h;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float maxITime = 3.;
    float iTimeFactor = maxITime - mod(iTime,maxITime)/2.;
    int vCx = int(abs(fragCoord.x-(iResolution.x/2.))*iTimeFactor);
    int vCy = int(abs(fragCoord.y-(iResolution.y/2.))*iTimeFactor);
    float h = float(ham(vCx, vCy))/10.;
    fragColor = vec4(vec3(sin(h*iTime),sin(h*4.*iTime),sin(h*5.)),1.0);
}
