void mainImage( out vec4 C, in vec2 fragCoord )
{
    float y=fragCoord.y/iResolution.y;

    float stop1=0.4;
    float stop2=0.5;
    float stop3=0.8;

    vec3 C1=vec3(0.3,0.4,0.9);
    vec3 C2=vec3(0.9);
    vec3 C3=vec3(0.2,0.8,1)*0.8;

    C.w=1.0;

    C.xyz=C1;
    C.xyz=mix(C.xyz,C2,smoothstep(stop1,stop2,y));
    C.xyz=mix(C.xyz,C3,smoothstep(stop2,stop3,y));
    //C.xyz=normalize(C.xyz);

}
