vec2 minres = vec2(min(iResolution.x, iResolution.y));
vec2 halfRes = iResolution.xy / vec2(2.0);


vec3 bgColor = vec3(0.2, 0.3, 0.5);
vec3 heartColor = vec3(8.0, 0.2, 0.2);


float soundAmp1 = texture(iChannel0, vec2(0.1, 0.25)).x;
float soundAmp2 = texture(iChannel0, vec2(0.5, 0.25)).x;
float soundAmp3 = abs(soundAmp1 - soundAmp2);


vec2 getXY() {
	vec2 xy = (fragCoord.xy - halfRes) / minres;
	return xy;
}


float distHeart(vec2 pos, float scale) {
	float amp = soundAmp3;

	float size = 1.0 * cos(amp);
	float sizePow = size < 0.0 ? 8.0 : 67.0;
	size = 0.7 + 0.3 * abs(pow(size, sizePow));
	pos /= (scale * size);

	pos.y -= 0.3;
	float xx1 = pos.x * 7.0;
	pos.y += 0.1 * cos(abs(xx1));
	float xx2 = max(0.0, 0.5 - abs(pos.x));
	pos.y += 0.5 * abs(xx2);
	float r = length(pos) * 3.0;

    return 1.0 - clamp(75.0 * (1.0 - r), 0.0, 1.0);
}


float distSmallHearts(vec2 pos) {
	float dis = 1.0;
	int i = 0;
	for (int i = 0 ; i < 32 ; ++i) {
		float ii = 6.28 * float(i) / 32.0;
		float jj = ii;
		float kk = 5.0 * ii;
		float x = cos(jj) + 0.15 * sin(kk + 8.0 * iTime);
		float y = 1.4 * sin(jj + 0.7 * iTime) + 0.35 * cos(kk + 5.4 * iTime);
		vec2 xy = 0.002 * halfRes * vec2(x,y);
		dis = min(dis, distHeart(pos + xy, 0.1));
	}
	return dis;
}


float distHearts(vec2 pos) {
	float dis = 1.0;

	vec2 smallPos = pos * 1.5 + vec2(0.66, 0.0);
	vec2 bigPos = pos * 1.5 + vec2(-0.66, 0.0);

	dis = min(dis, distSmallHearts(smallPos));
	dis = min(dis, distHeart(bigPos, 1.75));
	return dis;
}


vec3 bgShadedColor() {
	vec2 off = vec2(0.01 * iTime, 0);
	vec2 pos = 0.5 * fragCoord.xy / iResolution.xy;
    vec3 rgb = texture(iChannel1, off + pos).xyz;
	float b = (rgb.x + rgb.y + rgb.z) * 0.33;
	b = 0.1 + 1.0 * b;
	return b * bgColor;
}


vec3 heartShadedColor() {
	vec2 off = vec2(0.01 * iTime, 0);
	vec2 pos = 0.15 * fragCoord.xy / iResolution.xy;
    vec3 rgb = texture(iChannel1, off + pos).xyz;
	float b = (rgb.x + rgb.y + rgb.z) * 0.33;
	b = 0.5 + 2.0 * b;
	return b * heartColor;
}


vec3 filmic(vec3 x) {
	x *= 0.3;
	x = max(vec3(0.0) , x-0.004);
	return (x*(6.2*x+.5))/(x*(6.2*x+1.7)+0.06);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 pos = getXY();

	vec3 col = mix(heartShadedColor(), bgShadedColor(), distHearts(pos));
	fragColor = vec4(filmic(col), 1.0);
}
