void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.yy;
    vec2 realCenter = fragCoord.xy / iResolution.xy;
    vec2 center = vec2(0.5+0.15*cos(iTime*1.05343),0.5+0.15*sin(iTime));
    vec2 dist = uv-center;
    float d = sqrt(dot(dist,dist));
    d = pow((d+0.86),1000.0);
    if(uv[0]>0.9 &&
       uv[0]<1.2 && 
       uv[1]>0.35&&
       uv[1]<0.65) { d = 0.0; }
    float gg = 0.5*pow(sin(uv[0]*10.0+
                      iTime+
                      0.25*sin(5.0*iTime)),100.0);
	fragColor = vec4(1.0-d,
                        gg,
                        0,
                        1.0);
}
