void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy  ;uv -= 0.5;uv.x *= (iResolution.x /iResolution.y);

    vec2 RadUv = vec2(0.,0.);    
	RadUv.x = ((atan(uv.x,uv.y) * 0.15915494309189533576888376337251 /*OneOverTau*/) ) +0.5;
    RadUv.y = length(uv);


 	//fragColor = texture(iChannel0,RadUv);
	fragColor = mix(vec4(RadUv.xy,0.,1.),texture(iChannel0,RadUv + vec2(00.,iTime)),(sin(iTime) + 0.5) * 0.5);
}
