void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	 vec2 uv = fragCoord / iResolution.y;

    //CONSTANTS
    vec3 RED=vec3(1.0,0.,0.);
    float SUN_RADIUS=0.20;
    vec2 SUN_CENTER=uv-vec2(0.5,0.5);
    float NUM_RAYS=16.;
    float ROTSPEED=2.;

    vec3 color=vec3(1.0);
    if(cos(atan(SUN_CENTER.y,SUN_CENTER.x)*NUM_RAYS+iTime*ROTSPEED)<0.0)
        color=RED;
    if(length(SUN_CENTER)<SUN_RADIUS)
        color=RED;
	fragColor = vec4(color,1.0);
}
