void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 mouseCC = iMouse.xy/iResolution.xy;
	vec4 tex =texture(iChannel0,uv);

	vec2 offset = uv * 2.0 * tex.xy;
	vec4 colorOut=vec4(0.0);
	float bgalpha=0.04;
	float scale =0.5;
	//spike 1:
	float cc = sqrt(cos(iTime-offset.x) * (uv.x- mouseCC.x) + sin(iTime-offset.y) * (uv.y- mouseCC.y));
	colorOut +=vec4(0.9,0.0,0.0,1.0)*cc;
	//spike 2:
	offset+=45.0 * 3.14/180.0;
	cc+=sqrt(cos(iTime- offset.x) * (uv.x- mouseCC.x) + sin(iTime- offset.y) * (uv.y- mouseCC.y));
	colorOut +=vec4(0.0,0.9,0.0,1.0)*cc;
	//spike 3:
	offset+=45.0 * 3.14/180.0;
	cc+=sqrt(cos(iTime- offset.x) * (uv.x- mouseCC.x) + sin(iTime- offset.y) * (uv.y- mouseCC.y));
	colorOut +=vec4(0.0,0.0,0.9,1.0)*cc;
	//spike 4:
	offset+=45.0 * 3.14/180.0;
	cc+=sqrt(cos(iTime- offset.x) * (uv.x- mouseCC.x) + sin(iTime- offset.y) * (uv.y- mouseCC.y));
	colorOut +=vec4(1.0,0.4,0.0,1.0)*cc;

	colorOut*= scale;

	float eq =sqrt( sin(tex.x) + cos(tex.y));//vec4()
	//Overlay:
	colorOut =1.0 -  (colorOut + cos(sin(iTime)+iResolution.x* uv.x * uv.y*25.0)*bgalpha );

	fragColor = colorOut ;

}
