#define Do3D
const float tau = 2.*acos(-1.);
void image( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy;

    vec3 c = vec3(1);
    vec2 q = uv*2.-1.;
    q.x*=iResolution.x/iResolution.y;
    float a = clamp(atan(q.x,q.y),0.,tau/2.)+(atan(q.x,q.y)>0.?tau/2.:.0)+clamp(atan(-q.x,-q.y),0.,tau/2.);
    float r2 = (a)/tau;

    float d =length(q);

    float r = r2;
    float s= iTime;
    r-=s;
    r=fract(r);


    float l = sin(s*2.)/2.+.5;
    l/=2.;
    l+=.25;


     if (d<.23) {
    	c=vec3(.2,0,1);
        if (r>l) {
			c=vec3(.1,1,.2);
        }    
    }
    if (abs(d-.25)<.02) {
        if (abs(r)<l) {
    		c=vec3(1,0,.2);

        }
    }

    if (abs(q.y+.4)<.02-abs(q.x)/150.) {
        float b = sin((q.x-s)*4.);

        float a=sign(b);
        c=mix(vec3(.1,1,.2),vec3(1,0.,.2),a);
        if (b<-.5) {
        	c=vec3(.2,0,1);    
        }
    }

	fragColor = vec4(c,1.0);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 ms = texture(iChannel0, vec2(0)).rg/iResolution.xy-.5;
    uv+=ms/5.;
    vec4 c = vec4(0);
    vec4 c2 = vec4(0);
    image(c,uv);
    float z = .01;
    float a = -1.;
    #ifndef Do3D
    a=1.;
    #endif
    vec2 of = uv+a*normalize(ms)/100.;
    image(c2,of);
    if(c2!=vec4(1)) {
        #ifdef Do3D
    	c2/=2.;
        #endif
        #ifndef Do3D
        c2=vec4(.3);
        #endif
    }
    vec4 c3 = c;
    if (c==vec4(1)) {
    	c3=c2;    
    }
	fragColor = c3;
}

