renderpass.code:
bool isPressed(float keyCode) {
        keyCode = (keyCode + 0.5) / 256.0;
        vec2 uv = vec2(keyCode, 0.25);
        float key = texture(iChannel0, uv).r;

        return key > 0.0;
}

bool isToggled(float keyCode) {
        keyCode = (keyCode + 0.5) / 256.0;
        vec2 uv = vec2(keyCode, 0.75);
        float key = texture(iChannel0, uv).r;

        return key > 0.0;
}

#define KEY_A 65.0
#define KEY_1 49.0
#define KEY_RIGHT_ARROW 39.0

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec3 col = vec3(0.0);
    if (isToggled(KEY_A)) {
        col.r = 0.7;
    }
    if (isPressed(KEY_RIGHT_ARROW)) {
        col.b = 0.7;
    }

    vec2 uv = fragCoord.xy / iResolution.xy;
    col += vec3(texture(iChannel0, uv));

    fragColor = vec4(col, 1.0);
}
