float Circle(vec2 uv, vec2 offset, float radius, float blur){

    float d=length(uv-offset);
    return smoothstep(radius, radius-blur, d);
}

float Plot(vec2 uv, float point){

	return smoothstep(point, point, uv.x)-smoothstep(point+0.05, point, uv.x);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


	// UV
    vec2 uv = fragCoord/iResolution.xy;
    uv-=0.5;
    uv.x*=iResolution.x/iResolution.y;
    uv*=10.0;

    // Position of the curve
	uv.y+=2.0;


    // Big Circles
    vec4 circle =vec4(Circle(uv, vec2(0.0, 2.0+sin(iTime)*0.10), 2.5, 0.15));
    vec4 glow=vec4(Circle(uv, vec2(0.0, 2.0+sin(iTime)*0.10), 6.0, 10.0))*vec4(1.0, 1.0, 1.0, 1.0)*pow(sin(iTime),2.0);


    // Dots
    float blackDot=Circle(uv, vec2(-0.5,3.2+sin(iTime)*0.10), 0.40, 0.1);
    float whiteDot=Circle(uv, vec2(0.5, 0.9+sin(iTime)*0.10), 0.40, 0.1);

	float t=sin(uv.y+1.2);
    vec4 background=vec4(0.2, 0.2, 0.3, 1.0);

    vec4 yinyang=vec4(Plot(-uv, t))+vec4(0.4);

	vec4 combined=mix(circle, yinyang, circle.g);



    combined=mix(background+glow, combined, circle.a);
    vec4 bd=mix(vec4(blackDot), vec4(2.0, 2.0, 2.0, 2.0), blackDot);
    vec4 wd=mix(vec4(whiteDot), vec4(2.0), whiteDot);
    fragColor=combined-bd+wd;

}
