void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 draw = texture(iChannel0,uv);
    fragColor = vec4(draw.rgb,1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    float time = iTime;
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 colors = vec3(0.);
    float scanline = (mod(iTime / 10.0, 1.0));
    if ( scanline < uv.x + (2./iResolution.x) && scanline > uv.x) {
        colors = texture(iChannel0,uv).rgb;
    } else {
        colors = texture(iChannel1,uv).rgb;
    }
    fragColor = vec4(colors,1.0);
}
