void mainImage( out vec4 fragColor, in vec2 fragCoord ){	
	vec2 uv = fragCoord.xy / iResolution.xy;
	float distance = sqrt( pow(abs(uv[0]-0.5), 0.5) + pow(abs(uv[1]-0.5), abs(sin(iTime/5.)*0.025)) );
	float color = sin(distance*iTime*10.0) * cos(distance*iTime*0.05);
	fragColor = vec4(color, color, color,1.0);
}
