#define PI 3.141596

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord.xy / iResolution.xy;
    vec2 q = p - vec2(0.5, 0.5);
    p.x *= iResolution.x / iResolution.y; // Make it square

    vec3 colour = texture(iChannel0, p.xy).rgb;
    float spot = cos(6.0*PI*iTime);

    colour += spot;

    fragColor = vec4(colour,  1.0);
}
