float getCheckerboard(vec2 uv) {
	if (uv.x > 0.5) {
		if (uv.y > 0.5) {
			return 1.0 ;
		} else {
			return 0.0 ;
		}
	} else {
		if (uv.y > 0.5) {
			return 0.0 ;
		} else {
			return 1.0 ;
		}
	}
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 world = fragCoord.xy / iResolution.xy;
	// Simulate we are moving
	world.x = world.x + iTime/16.0 ;
	world.y = world.y + sin(mod(iTime, 6.2)) / 4.0 ;

	vec2 uv1 = mod(world*8.0, 1.0) ;
	float colorComp1 = getCheckerboard(uv1) ;

	vec2 uv2 = mod(world*16.0, 1.0) ;
	float colorComp2 = getCheckerboard(uv2) ;

	vec2 uv3 = mod(world*32.0, 1.0) ;
	float colorComp3 = getCheckerboard(uv3) ;

	float colorComp = (4.0*colorComp1 + 2.0*colorComp2 + colorComp3) / 7.0;

	float red = clamp(colorComp-0.05, 0.0, 1.0) ;
	float green = clamp(colorComp, 0.0, 1.0) ;
	float blue = clamp(colorComp+0.1, 0.0, 1.0) ;
	fragColor = vec4(red,green,blue,1.0);
}
