void mainImage( out vec4 O,  vec2 U )
{
vec2 R = iResolution.xy;
	float C = (1.-U.y/R.y);
    U =5.* ( U+U-R) / R.y;         // normalized coordinates
    U.y = 1.-3.*U.y;                   // swap vertical
    U /= 1.+U.y/10.;                // perspective
    U.y -= .5*iTime;
    vec2 UA = U;
    vec2 UB = U;
    U = abs(fract(U)- .50);			// distance to axis
    UA = abs(fract(UA+C/15.)-.50);
    UB = abs(fract(UB+C/30.)-.50);
    U = .1*C/ sqrt(U);                // turn to blured line
    UA = .1*C/ sqrt(UA);
    UB = .1*C/ sqrt(UB);
    O = (U.x+U.y) * vec4(0,0.,.7,0)
        +                 vec4(.3,.2,.2,0) 
        + (UA.x+UA.y)   * vec4(.7,0.,0.,0)
        + (UB.x+UB.y)   * vec4(0.,.7,0.,0) ; // combine H&V + color * inverted fade
    O *= C;
}
