#define PI 3.14159
#define TWO_PI 6.28319
#define PHI 0.618
#define SAMPLE_POINT_LOW vec2 (0.013, 0.25)
#define SAMPLE_POINT_HIGH vec2 (0.9, 0.2)

float random (vec2 co) {
   return fract (sin (dot (co.xy, vec2 (12.9898,78.233))) * 43758.5453);
}


vec4 getBass () {
    return normalize (texture (iChannel0, SAMPLE_POINT_LOW));    
}

vec4 getTreble () {
    return normalize (texture (iChannel0, SAMPLE_POINT_HIGH));    
}

float superformula (float angle, vec4 shape, vec2 symmetry) {    
    float m = shape.x;
    float n1 = shape.y;
    float n2 = shape.z;
    float n3 = shape.w;
    float a = symmetry.x;
    float b = symmetry.y;

    float rpart = (m * angle) / 4.;
    float apart = abs (cos (rpart) / a);
    float bpart = abs (sin (rpart) / b);

    float r = pow (
        pow (apart, n2) + pow (bpart, n3),
        -1. / n1
    );

    return r;
}

float angleBetween (vec2 a, vec2 b) {
    float cosTheta = dot (a, b) / (length (a) * length (b));

    return acos (cosTheta);
}

vec4 createShape (float t, vec4 music) {
    vec4 treble = getTreble ();
    vec4 bass = getBass ();
    vec4 nt = normalize (treble);

    float m = 1. + (mix (3., 0., bass.z) + mix (1., 0., music.z + treble.z));
    float n1 = 1. + (mix (1., 0., bass.z) + mix (5., 0., music.z + treble.z));
    float n2 = 1. + (mix (2., 0., bass.z) + mix (5., 0., music.z + treble.z));
    float n3 = 1. + (mix (4., 0., bass.z) + mix (1., 0., music.z + treble.z));

    return vec4 (m, n1, n2, n3);
}

vec2 createSymmetry (float t) {
    vec4 music = getBass ();

    float x = 1.;
    float y = 1.;

    return vec2 (x, y) * music.z;
}

vec4 crazyNoise (vec4 music, vec2 fragCoord, float t) {    
    vec4 noise = vec4 (
        abs (tan (random (fragCoord + music.xy) + music.z)) + 0.1 * sin (t),
        abs (sin (random (fragCoord + music.yx) + music.z)) + 0.1 * sin (t),
        abs (tan (random (fragCoord + music.yx) + music.z)) + 0.1 * sin (t),
        1.
    );

    return noise;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) {
    float radius = iResolution.y / 2.;

    vec2 center = iResolution.xy / 2.;
    vec2 d = fragCoord - center;
    vec2 up = vec2 (0., 1.);

    float t = iTime * 0.6 + 100.;
    float tTrack = iChannelTime[0] + 1000.;
    float angle = angleBetween (up, d);
    vec4 music = (2. * getTreble () + 5. * getBass ()) / 7.;
    vec2 symmetry = createSymmetry (t);
    vec4 shape = createShape (iChannelTime[0], music);
    float r = superformula (angle, shape, symmetry) * radius;

    float crazyPattern = tan (angle * tTrack + tTrack) + music.x;
    //fragColor = cos (length (music)) * crazyPattern * vec4 (PHI);
    fragColor = (crazyPattern) * vec4 (PHI);
    //fragColor -= crazyNoise (music, fragCoord, t);
    if (length (d) <= r) {
        float dr = r / length (d);

        vec4 c = normalize (shape);
        //c.r = mix (1. - c.r, c.r, dr);
        //c.g = mix (0., 1. - c.g, dr);
        //c.b = 1. - c.b * dr;
        //c.a = mix (0.6, 1., dr);

        fragColor = (1. - dr) * fragColor + crazyPattern * c;
    }

}
