// based on https://www.shadertoy.com/view/Md2GDw
// original glsl shader by kusma

float scale = 0.03;
float b_threshold = 0.4;
float l_threshold = 0.5;
float rgb_offset = 5.0;
float bias = 0.3;
float opacity = 1.0;

vec3 difference( vec3 s, vec3 d )
{
	return abs(d - s);
}

float rand2(vec2 co) 
{
	return fract(sin(dot(co.xy,vec2(12.9898,78.233))) * 43758.5453);
}

vec3 rand1(vec2 uv) {
	vec2 c = ((.01)*iResolution .x)*vec2(1.,(iResolution .y/iResolution .x));
	vec3 col = vec3(0.0);
   	float r = rand2(vec2((2.) * floor(uv.x*c.x)/c.x, (2.) * floor(uv.y*c.y)/c.y ));
   	float g = rand2(vec2((5.) * floor(uv.x*c.x)/c.x, (5.) * floor(uv.y*c.y)/c.y ));
   	float b = rand2(vec2((9.) * floor(uv.x*c.x)/c.x, (9.) * floor(uv.y*c.y)/c.y ));

	col = vec3(r,g,b);

	return col;
}


// Using Ashima's simplex noise
//     License : Copyright (C) 2011 Ashima Arts. All rights reserved.
//               Distributed under the MIT License. See LICENSE file.
//               https://github.com/ashima/webgl-noise

vec3 mod289(vec3 x) {
  return x - floor(x * (1.0 / 289.0)) * 289.0;
}

vec2 mod289(vec2 x) {
  return x - floor(x * (1.0 / 289.0)) * 289.0;
}

vec3 permute(vec3 x) {
  return mod289(((x*34.0)+1.0)*x);
}

float snoise(vec2 v)
  {
  const vec4 C = vec4(0.211324865405187,  // (3.0-sqrt(3.0))/6.0
                      0.366025403784439,  // 0.5*(sqrt(3.0)-1.0)
                     -0.577350269189626,  // -1.0 + 2.0 * C.x
                      0.024390243902439); // 1.0 / 41.0
// First corner
  vec2 i  = floor(v + dot(v, C.yy) );
  vec2 x0 = v -   i + dot(i, C.xx);

// Other corners
  vec2 i1;
  //i1.x = step( x0.y, x0.x ); // x0.x > x0.y ? 1.0 : 0.0
  //i1.y = 1.0 - i1.x;
  i1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
  // x0 = x0 - 0.0 + 0.0 * C.xx ;
  // x1 = x0 - i1 + 1.0 * C.xx ;
  // x2 = x0 - 1.0 + 2.0 * C.xx ;
  vec4 x12 = x0.xyxy + C.xxzz;
  x12.xy -= i1;

// Permutations
  i = mod289(i); // Avoid truncation effects in permutation
  vec3 p = permute( permute( i.y + vec3(0.0, i1.y, 1.0 ))
		+ i.x + vec3(0.0, i1.x, 1.0 ));

  vec3 m = max(0.5 - vec3(dot(x0,x0), dot(x12.xy,x12.xy), dot(x12.zw,x12.zw)), 0.0);
  m = m*m ;
  m = m*m ;

// Gradients: 41 points uniformly over a line, mapped onto a diamond.
// The ring size 17*17 = 289 is close to a multiple of 41 (41*7 = 287)

  vec3 x = 2.0 * fract(p * C.www) - 1.0;
  vec3 h = abs(x) - 0.5;
  vec3 ox = floor(x + 0.5);
  vec3 a0 = x - ox;

// Normalise gradients implicitly by scaling m
// Approximation of: m *= inversesqrt( a0*a0 + h*h );
  m *= 1.79284291400159 - 0.85373472095314 * ( a0*a0 + h*h );

// Compute final noise value at P
  vec3 g;
  g.x  = a0.x  * x0.x  + h.x  * x0.y;
  g.yz = a0.yz * x12.xz + h.yz * x12.yw;
  return 130.0 * dot(m, g);
}


float n_scale = clamp(rand2(vec2((iTime), (iTime) * floor(2.0)))* 5.0, 0.0, scale);
float n_g_noise = rand2(vec2((iTime), (5.+iTime) * floor(300.0))) * 3123.0;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec3 original = texture(iChannel0, uv).rgb;
	vec2 block = floor(fragCoord.xy / vec2(n_scale));
	vec2 uv_noise = block / vec2(n_g_noise);
	uv_noise += floor(vec2(iTime) * vec2(1234.0, 3543.0)) / vec2(64);
	vec3 d_noise = rand1(uv_noise);
	float block_thresh = pow(fract(iTime * 1236.0453), b_threshold);
	float line_thresh = pow(fract(iTime * 2236.0453), l_threshold);
	vec2 uv_r = uv, uv_g = uv, uv_b = uv;

	// glitch some blocks and lines
	// if (texture(Noise, uv_noise).r < block_thresh ||
	if (d_noise.r < block_thresh ||
		//texture(Noise, vec2(uv_noise.y, 0.0)).g < line_thresh) {
		rand1(vec2(uv_noise.y, 0.0)).g < line_thresh)
	{
		vec2 dist = (fract(uv_noise) - 0.5) * 0.3;
		uv_r += dist * 0.01 * rgb_offset;
		uv_g += dist * 0.02 * rgb_offset;
		uv_b += dist * 0.0125 * rgb_offset;
	}
	fragColor.r = texture(iChannel0, uv_r).r;
	fragColor.g = texture(iChannel0, uv_g).g;
	fragColor.b = texture(iChannel0, uv_b).b;

	vec3 col = fragColor.rgb;
	// loose luma for some blocks
	// if (texture(Noise, uv_noise).g < block_thresh)
	if (d_noise.g < block_thresh)
		col.rgb = col.ggg;

	// discolor block lines
	// if (texture(Noise, vec2(uv_noise.y, 0.0)).b * 3.5 < line_thresh)
	if (rand1(vec2(uv_noise.y, 0.0)).b * 3.5 < line_thresh)
		col.rgb = vec3(0.0, dot(col.rgb, vec3(1.0)), 0.0);

	// interleave lines in some blocks
	// if (texture(Noise, uv_noise).g * 1.5 < block_thresh ||
	if (d_noise.g * 1.5 < block_thresh ||
	//	texture(Noise, vec2(uv_noise.y, 0.0)).g * 2.5 < line_thresh) {
		rand1(vec2(uv_noise.y, 0.0)).g * 2.5 < line_thresh ) 
	{
		float line = fract(fragCoord.y / 3.0);
		vec3 mask = vec3(3.0, 0.0, 0.0);
		if (line > 0.333)
			mask = vec3(0.0, 3.0, 0.0);
		if (line > 0.666)
			mask = vec3(0.0, 0.0, 3.0);
		col = col.rgb * mask;
	}

    float blend = snoise(vec2(iTime*100.0));
    blend = clamp((blend-(1.0-bias))*999999.0, 0.0, opacity);

	col = mix(original, col, blend);
	fragColor = vec4(col, 1.0);
}
