void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 cursor = vec2(iMouse.x,20.0);
	float distanceToCursor = distance(cursor,fragCoord.xy);
	float distanceToLine= abs(fragCoord.y-20.0);

	if(distanceToCursor<6.0)
	{
		fragColor = vec4(1,1,0,0);
	}

	else if (distanceToLine<3.0)
	{
		fragColor = vec4(0,0,1,0);
	}
	else
	{
		float x = iMouse.x/10.0;

        if (iMouse.x==0.0)
        {
            x =iResolution.x/20.0;
            cursor.x = x;
        }

        vec2 modul = mod(fragCoord,x);
		vec2 normalizedCord= vec2(fragCoord-modul)/iResolution.xy;
		vec4 color = texture(iChannel0, normalizedCord);
		fragColor =color;
	}




}
