// Ripple effect by Mahmud Yuldashev. Math from Adrian Boeing Blog

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //variables
    vec2 mouse=iMouse.xy;
    float time=iTime;

    float intensity=0.09;


	vec2 p =-1.+2.*fragCoord.xy / iResolution.xy-vec2(0,-mouse.y*.001);


    float cLength=length(p);

    vec2 uv=(fragCoord.xy / iResolution.xy)/-1.
        +(p/cLength)*cos(cLength*15.0-iTime*4.0)*intensity;

    vec3 col=smoothstep(0.1,.91,texture(iChannel0,uv).xyz);

    col.r=0.01;
    col.g=0.;




	fragColor = vec4(col,1.0);

}
