float Sphere(vec2 uv, float r, float blur){

    float d=length(uv);
    return smoothstep(r, r-blur, d);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
    uv-=0.5;
    uv.x*=iResolution.x/iResolution.y;
	float time=mod(iTime, 2.0);

    vec4 distortion=vec4(vec3(Sphere(uv, sin((pow(time,2.0))), 0.2)),1.0);

	vec4 table=mix(texture(iChannel1, uv*distortion.r+time*0.5), 
                   texture(iChannel0, uv), 
                   1.0-distortion);

    // Output to screen
	fragColor=table*vec4(1.0, 1.0, 1.0, 1.0);

}
