void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   	// Inputs - Play with these values.
    const int steps = 50;
    const float scale = 2.5;
    const vec2 pan = vec2(0.6, 0.5);
    ////////////////////////////////

    vec2 c = fragCoord.xy / iResolution.xy;

    // map from [0,1] to [-0.5,0.5] then apply pan and scale.
    c = (c - pan) * scale;

	// apply aspect ratio to fix distortion     
    c.x *= iResolution.x / iResolution.y;

    int currStep = 0;

    // modelling complex number with vec2
    // z = x + yi
    vec2 z = vec2(0.0, 0.0);

    for (int i = 0; i < steps; i++)
    {
        // step: z = zÂ² + c 
        // z = (xÂ² + 2xyi - yÂ²) + (cx + cyi)
        // zx = (xÂ² - yÂ² + cx)
        float x = (z.x * z.x - z.y * z.y) + c.x;

        // zy = 2xy + cy
        float y = 2.0 * z.x * z.y + c.y;

        if ((x * x + y * y) > 4.0) break;
        z.x = x;
        z.y = y;

        currStep++;
    }

    float r = float(currStep) / float(steps);
	fragColor = (currStep < steps) ? vec4(r, 0.0, 0.0, 0.0) : vec4(0.0, 0.0, 0.0, 1.0);
}
