highp float rand(vec2 co) {
    return fract(sin(mod(dot(co.xy ,vec2(12.9898,78.233)),3.14))*43758.5453);
}
float tnoise(vec2 co){
    vec2 w=co;
    co.y+=co.x/2.;
    const vec2 s=vec2(1.,0.);
	vec2 p=floor(co);
    if(fract(co.x)<fract(co.y))p+=0.5;    
  	return rand(p);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = (fragCoord*2.-iResolution.xy)/40.;
    float n=tnoise(uv);
    fragColor = vec4(sin(iTime*n*7.+n*3.141592653589793*2.)*0.5+0.5)*0.3+0.5;
    fragColor+=sin((uv.x-uv.y)*30.)/2.;
    fragColor+=rand(uv)/2.;
    fragColor/=4.;
}
