void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-iResolution.xy*.5)/iResolution.y;
	uv.y*=1.2;
    uv.x/=1.;
    uv.y=uv.y+sin(iTime*2.)*0.2-max(sqrt(abs(uv.x/2.+sin(iTime*3.)*0.05)),0.05)*0.95;
    //uv.x+=cos(uv.y*20.+iTime*5.)/100.;
   uv.y+=0.12;
    //uv.y+=iTime/10.;

    float k =clamp(pow( max(0.18 - dot(uv,uv), 0.), 1.2),0.0,1.);


    fragColor = vec4(k)*exp(1.4)*vec4(1.8,1.,1.,1.);

}
