void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = 1.15*(-iResolution.xy+2.0*fragCoord.xy)/iResolution.y;

	vec2 z = p;
	float f = 4.;
	float g = 4.;

	for( int i=0; i<128; i++ ) 
	{
		float w = float(i)*1.32457+iTime;
		vec2 z1 = vec2(2.*cos(w),2.*sin(w));		   
		z = vec2( z.x*z.x-z.y*z.y, 2.0*z.x*z.y ) + p;
		f = min( f, abs(dot(z-p,z-p) -.004*float(i)));
		g = min( g, dot(z-z1,z-z1));
	}

	f = 1.0+log(f)/15.0;
	g = 1.0+log(g)/8.0;

	fragColor = texture(iChannel0, vec2(1.-f,1.-g));

	fragColor += texture(iChannel0, vec2(sin(f),cos(g)));


	fragColor /= 2.;
}
