#define M_PI (3.14159265359)

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec2 gRes = iResolution.xy;
	vec2 gPos = fragCoord.xy/gRes;
	vec2 cRes = iChannelResolution [0].xy;	
	vec2 cPos = (gPos - vec2 (0.5, 0.5))*2.0;

	float r = distance (cPos, vec2 (0.0));
	float a = atan (cPos.x, cPos.y) / (M_PI * 2.0);

	float r0 = mod (r + iTime/4.0, 1.0) ;
	float a0 = mod (a + iTime/8.0 + r, 1.0) ;

	float r1 = mod (r*0.125 + iTime/8.0, 1.0) ;
	float a1 = mod (a, 1.0) ;

	vec4 _0 = texture(iChannel0, vec2 (r0, a0));
	vec4 _1 = texture(iChannel1, vec2 (r1, a1));
	fragColor =  _0 + _1*(1.0 - r)*0.6 + (1.0 - r)*0.7;

}
