vec3 blackbody(float t){
    // http://en.wikipedia.org/wiki/Planckian_locus

    const vec4 vx = vec4( -0.2661239e9, -0.2343580e6, 0.8776956e3, 0.179910   );
    const vec4 vy = vec4( -1.1063814,   -1.34811020,  2.18555832, -0.20219683 );
    //vec4 vy = vec4(-0.9549476,-1.37418593,2.09137015,-0.16748867); //>2222K
    float it = 1. / t;
    float it2= it * it;
    float x = dot( vx, vec4( it*it2, it2, it, 1. ) );
    float x2 = x * x;
    float y = dot( vy, vec4( x*x2, x2, x, 1. ) );
    float iy = 1./y;

    // http://www.brucelindbloom.com/index.html?Eqn_RGB_XYZ_Matrix.html
    const mat3x2 xzTosrgb = mat3x2(
         3.2404542,-0.4985314,
        -0.9692660, 0.0415560,
         0.0556434, 1.0572252
    );
    const vec3 yTosrgb = vec3(-1.5371385, 1.8760108, -0.2040259);

    vec3 srgb = vec2( x*iy, iy-(x*iy+1.) )* xzTosrgb + yTosrgb;

    return max( srgb, 0. );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    fragColor.rgb = blackbody(1667.+uv.x*5000.)*0.3;

    fragColor.rgb=pow(fragColor.rgb,vec3(1./2.2));


}
