void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	uv *= 2.0;
	uv -= 1.0;	


	vec4 bgc = texture(iChannel0, uv*10.0);
	float p = sin(uv.x*4.0+uv.y+iTime*2.0)*0.1;
	uv += p;


	bgc *= 1.0-distance(uv, vec2(0.0))*0.8;



	fragColor = vec4(0.0);
	fragColor = 1.0-abs(uv.y) > 0.8 ? vec4(1.0,1.0,0.0,1.0):vec4(1.0,0.0,0.0,1.0);
	fragColor = 1.0-abs(uv.x+0.3) > 0.8 ? vec4(1.0,1.0,0.0,1.0):fragColor;

	fragColor -= p*4.0;

	fragColor = 1.0-sqrt(pow(uv.x,16.0)+pow(uv.y,16.0))>0.9?fragColor:bgc;
}
