void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 xy = fragCoord/iResolution.xy;

    xy.x = abs(xy.x * 1.1 - 0.55);
    xy.y = abs(xy.y - 0.5);
    xy.x = xy.x * xy.x;

	float a = fract(sin(iTime * 11.9));
    float b = fract(cos(iTime * 9.1));

    // Time varying pixel color
    float colr = 0.5 + 0.5*cos(iTime+xy.y + sin(abs(log(xy.x * xy.y) * iTime / sin(iTime)) * 5.0));
    float colg = 0.5 + 0.5*cos(iTime+xy.y + sin(abs(log(xy.x * xy.y) * iTime / 3.0) * 30.0 * (iTime / (1000.0 + iTime))));
    float colb = sin(colg * (colg + 0.5));

    colr = sin(colg * 4.0 + (colb / 200.0));
    colg = sin(colg + (xy.y / (iTime * 2000.0)));
    colb = 1.0 - sin(colg * 4.0 + (colb / 200.0)) - sin(colg + (xy.y / (iTime * 2000.0)));

    // Output to screen
    fragColor = vec4(
        colr,
        colg,
        colb, 
        1.0
    );
}
