// Generate Gold Noise image



void mainImage(out vec4 rgba, in vec2 xy)
{
    rgba = vec4(gold_noise(xy, fract(iTime)+1.0), // r
                gold_noise(xy, fract(iTime)+2.0), // g
                gold_noise(xy, fract(iTime)+3.0), // b
                1.0);                             // α
}

// Generate Gold Noise sound



vec2 mainSound(float time)
{
    return vec2(gold_noise(vec2(iSampleRate, iSampleRate)*(time/100.0), 1.0));
}

// Gold Noise ©2015 dcerisano@standard3d.com
// - based on the Golden Ratio
// - uniform normalized distribution
// - fastest static noise generator function (also runs at low precision)



const float PHI = 1.61803398874989484820459; // Φ = Golden Ratio 

float gold_noise(in vec2 xy, in float seed)
{
    return fract(tan(distance(xy*PHI, xy)*seed)*xy.x);
}
