// [2TC 15] Sand Pour Up
// Copyleft {c} 2015 Michael Pohoreski
// Version 1: // Chars: 170 repeat, 163 one-time
// Version 2: // Chars: 161 repeat, 155 one-time

#define D 10. // duration = 10 seconds

void mainImage(out vec4 f,in vec2 p) {
    vec2  q = p / iResolution.xy;
    float t = min( 1., mod(iTime,D) / D ); // repeat
//    float t = min( 1.,     iTime    / D ); // one-time
    q.y = 1. - ((q.y > t) ? t : q.y);
    f = texture(iChannel0,q);
}

