const vec2 CENTER_UV = vec2(0.5,0.5);



float sampleSong(float x) {
    return texture(iChannel1,vec2(x,0.05)).x*0.2;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy/iResolution.xy - CENTER_UV;
    uv *= 0.8;


    float dist = length(uv);

    float angle = atan(uv.y,uv.x);

    float sample_ = sampleSong(abs(angle)*0.4);

    fragColor = texture(iChannel0,vec2(angle,dist-(iTime*0.3)+sample_))+vec4(sample_*5.0);
}
