// New WebGL 2 version (252 chars)
//
#define t texture(iChannel0,p*.1,3.
void mainImage( out vec4 f, in vec2 p )
{
    vec4 q = p.xyxy/iResolution.y - .5, c=q-q;

    p.y = atan( q.x, q.y );

    for( float s=0.; s<.1; s+=.01 )
    {
        float x = length( q.xy ), z = 1.;

        for( ; z>0. && t).x<z ; z-=.01 )
            p.x = iTime*3. + s + 1./(x+x*z);

        f = c += t*x)*z*x*.2;
    }
}


