//---------------------------------------------------------
// Shader:   ColoredWaves.glsl         
// original: http://glslsandbox.com/e#5398.8
// added some mouse interaction
//---------------------------------------------------------
void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{
	float scale = iResolution.y / (40.0 + iMouse.y * 0.15);
	float ring = 25.0 + floor((iMouse.x * 0.01 - 5.5)* 2.0);	
	float radius = iResolution.x;
	float gap = scale * 0.5;
	vec2 pos = fragCoord.xy - iResolution.xy * 0.5;
	float d = length(pos);

	// Create the wiggle
	d += (sin(pos.y * 0.25 / scale + iTime) * 
          sin(pos.x * 0.25 / scale + iTime * 0.5)) * scale * 2.0;

	// Compute the distance to the closest ring
	float v = mod(d + radius / (ring * 2.0), radius / ring);
	v = abs(v - radius / (ring * 2.0) );
	v = clamp(v - gap, 0.0, 1.0);
	d /= radius;
	vec3 m = fract((d - 1.0) * vec3(ring * -0.5, -ring, ring * 0.25) * 0.5);

	fragColor = vec4(m*v, 1.0);
}
