void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv *= 0.51;
    float t = iTime*0.25 + 1.0;
    float s = mod(fract(uv.x * 3.+uv.x+t),0.05)*sin(t*10.)*100.;
    float r = tan( cos(uv.y+t)*100.);
    float m = tan(sin(uv.y + t*.3)*100.);

	fragColor = vec4(vec3(m/r*s),1.0);
}
