void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float col = mod(iTime, 0.5);
	vec3 color = vec3(col);
    fragColor = vec4(color,1.0);
}

vec2 mainSound(float time)
{
    float trigger = mod(time, 0.5);
    float pitch = 150.0;
    float envAmount = 5.0;
    float decay = 0.7;
    float kickPitch = trigger * (exp(-decay*trigger*envAmount));
    float kick = sin(6.2831*pitch*kickPitch)*exp(-3.0*trigger);


    return vec2( kick );
}
