bool voxel_thing(ivec4 b){
return (b.x>>b.y<<(b.z))%95<99-b.z;
}

bool voxel_thing1(ivec4 b){
    b.z += int(20.0+20.0*sin(iTime));
    b.y += int(iTime*10.0);
    return voxel_thing(b) && voxel_thing(ivec4(b.yxz,b.w));
}

void mainImage(out vec4 o,vec2 u)
{
    for(ivec4 b;voxel_thing1(b);)
    b = ivec4((u/iResolution.y-.5)*o.a-20.0,o+=.1);
    o /= vec4(1e2,4e3/o);
}
