// returns true if a point [uv] is within the circle defined by [origin, radius]
bool circle(vec2 uv, vec2 origin, float radius) {
 	return (length(uv-origin) < radius);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    float radius = sin(iTime)/2.;
    vec2 zero = vec2(sin(iTime)*0.3+0.5, cos(iTime)*0.3+0.5);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = length(uv-zero)+cos(iTime+vec3(0.,2.,.4));

    col.r += .5*atan(sin(iTime*0.002)*(uv.x+zero.x), length(uv-zero));
    col.b += atan(uv.y-zero.y, length(uv-zero));
    col.g -= .5*atan(cos(iTime*0.2)*(zero.y-uv.x), length(uv-zero))-col.b;

	col += length(uv-zero);

    col.r = (length(uv-zero)*col.b+sin(iTime*.2)*col.g);

    // Output to screen
    fragColor = vec4(col,1.0);
}
